mapper.select:
SELECT *
FROM ${tableName}
WHERE 1=1
AND SITE_TYPE = #{siteType}
AND ACCOUNT = #{account}
AND STATUS = 'T'
AND LAST_USE_DATE < SYSDATE - 1

mapper.update:
UPDATE ${tableName}
SET LAST_USE_DATE  = #{lastUseDate},
USE_HOST = #{useHost}
WHERE SITE_TYPE= #{siteType}
AND ACCOUNT = #{account}
